mapper.select:
select DI.id as displayInfoId , DI.place_name as placeName , PD.content as productContent , PD.description as productDescription , PD.id as productId , concat('img/', FI.file_name) as productImageUrl from product as PD , display_info as DI , product_image as PI , file_info as FI where PD.id= DI.product_id and PD.id= PI.product_id and PI.file_id= FI.id and FI.file_name like '%_ma_%'
and PD.category_id= #{categoryId}
limit #{start}, 4;
